import React, { useEffect, useState } from 'react';
import api from '../api/axios';
import NoteForm from '../components/NoteForm';
import NoteItem from '../components/NoteItem';

export default function Dashboard(){
  const [notes, setNotes] = useState([]);
  const [editing, setEditing] = useState(null);
  const [err, setErr] = useState('');

  const fetchNotes = async () => {
    try {
      const res = await api.get('/notes');
      setNotes(res.data);
    } catch (e) {
      setErr('Could not load notes');
    }
  };

  useEffect(()=>{ fetchNotes(); }, []);

  const addNote = async (note) => {
    try {
      const res = await api.post('/notes', note);
      setNotes(prev => [res.data, ...prev]);
    } catch (e) { setErr('Add failed'); }
  };

  const updateNote = async (note) => {
    try {
      const res = await api.put(`/notes/${note._id}`, note);
      setNotes(prev => prev.map(n => n._id === res.data._id ? res.data : n));
      setEditing(null);
    } catch (e) { setErr('Update failed'); }
  };

  const deleteNote = async (id) => {
    if (!window.confirm('Delete this note?')) return;
    try {
      await api.delete(`/notes/${id}`);
      setNotes(prev => prev.filter(n => n._id !== id));
    } catch (e) { setErr('Delete failed'); }
  };

  return (
    <div>
      <h2>Your Notes</h2>
      {err && <p className="error">{err}</p>}

      <div className="form-area">
        <h3>{editing ? 'Edit Note' : 'Add Note'}</h3>
        <NoteForm onSubmit={editing ? updateNote : addNote} initial={editing} />
        {editing && <button onClick={()=>setEditing(null)}>Cancel</button>}
      </div>

      <div className="notes-list">
        {notes.length === 0 ? <p>No notes yet.</p> : notes.map(note => (
          <NoteItem key={note._id} note={note} onEdit={(n)=>setEditing(n)} onDelete={deleteNote} />
        ))}
      </div>
    </div>
  );
}
