import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const token = localStorage.getItem("token");

  const fetchNotes = async () => {
    const res = await axios.get("http://localhost:5000/api/notes", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setNotes(res.data);
  };

  useEffect(() => {
    if (!token) navigate("/login");
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/notes/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchNotes();
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navbar */}
      <div className="flex justify-between items-center bg-white shadow px-6 py-4">
        <h1 className="text-2xl font-bold text-indigo-600">ðŸ“’ My Notes</h1>
        <button
          onClick={() => {
            localStorage.removeItem("token");
            navigate("/login");
          }}
          className="bg-red-500 text-white px-4 py-2 rounded-xl hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      {/* Notes Grid */}
      <div className="p-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {notes.length === 0 ? (
          <p className="text-center text-gray-500 col-span-full">No notes yet. Click + to add one!</p>
        ) : (
          notes.map((note) => (
            <div key={note._id} className="bg-white p-5 rounded-xl shadow-md hover:shadow-lg transition">
              <h3 className="text-lg font-semibold text-gray-800">{note.title}</h3>
              <p className="text-gray-600 mt-2">{note.description}</p>
              <div className="flex justify-end mt-4 space-x-2">
                <button className="px-3 py-1 bg-yellow-400 rounded-lg text-sm">Edit</button>
                <button
                  onClick={() => handleDelete(note._id)}
                  className="px-3 py-1 bg-red-500 text-white rounded-lg text-sm"
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Floating Add Note Button */}
      <button
        onClick={() => alert("Add Note Modal Coming Soon")}
        className="fixed bottom-8 right-8 bg-indigo-600 text-white p-4 rounded-full shadow-xl hover:bg-indigo-700 text-2xl"
      >
        +
      </button>
    </div>
  );
}
