import React, { useState } from 'react';
import api from '../api/axios';
import { useNavigate } from 'react-router-dom';

export default function Register(){
  const [form,setForm] = useState({name:'', email:'', password:'', role:'user'});
  const [err, setErr] = useState('');
  const nav = useNavigate();

  const onChange = e => setForm({...form, [e.target.name]: e.target.value});
  const onSubmit = async e => {
    e.preventDefault();
    try {
      const res = await api.post('/auth/register', form);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      nav('/');
    } catch (error) {
      setErr(error.response?.data?.message || 'Registration failed');
    }
  };

  return (
    <div className="auth-box">
      <h2>Register</h2>
      {err && <p className="error">{err}</p>}
      <form onSubmit={onSubmit}>
        <input name="name" value={form.name} onChange={onChange} placeholder="Name (optional)" />
        <input name="email" value={form.email} onChange={onChange} placeholder="Email" required />
        <input name="password" type="password" value={form.password} onChange={onChange} placeholder="Password" required />
        <select name="role" value={form.role} onChange={onChange}>
          <option value="user">User</option>
          <option value="admin">Admin (use carefully)</option>
        </select>
        <button type="submit">Register</button>
      </form>
    </div>
  );
}
